
public class Num_Patterns
{
	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
				
		
		int first_number = 3;
		int second_number = 6;
		int third_number = 11;
		
		int first_output;
		int second_output;
		int third_output;
		int fourth_output;
		int fifth_output;
		int sixth_output;
		int seventh_output;
		int eighth_output;
		int ninth_output;
		int tenth_output;
		int eleventh_output;
		int twelveth_output;
	
		first_output = 2+3+(first_number+1);
		
		second_output = ((first_number + 1) * (first_number + 2) / 2) - 7;
		
		third_output = 3 + (2 * first_number);
		
		fourth_output = first_number  * first_number * first_number;
		
		System.out.println("Value of first number is :" + first_number + " , Outputs are:" +" " + first_output + " " + second_output + " " + third_output + " " + fourth_output);
		
		fifth_output = 2+3+(second_number+1);
		
		sixth_output = ((second_number + 1) * (second_number + 2) / 2) - 7;
		
		seventh_output = 3 + (2 * second_number);
		
		eighth_output = second_number  * second_number * second_number;
		
		System.out.println("Value of second number is :" + second_number + " , Outputs are:" +" " + fifth_output + " " + sixth_output + " " + seventh_output + " " + eighth_output);
		
		ninth_output = 2+3+(third_number+1);
		
		tenth_output = ((third_number + 1) * (third_number + 2) / 2) - 7;
		
		eleventh_output = 3 + (2 * third_number);
		
		twelveth_output = third_number  * third_number * third_number;
		
		System.out.println("Value of third number is :" + third_number + " , Outputs are:" +" " + ninth_output + " " + tenth_output + " " + eleventh_output + " " + twelveth_output);
		
	}

}	
